import React, { useState } from 'react';
import { jsPDF } from 'jspdf';

function PDFGenerator() {
    const [pdfUrl, setPdfUrl] = useState('');

    const [futureDateDetails, setFutureDateDetails] = useState(() => {
        const today = new Date();
        const futureDate = new Date(today);
        futureDate.setDate(today.getDate() + 14); // Calculate 14 days in the future

        return {
            year: futureDate.getFullYear(),
            month: futureDate.getMonth() + 1, // Normalize month to 1-based index
            day: futureDate.getDate()
        };
    });


    const generatePdf = () => {
        const pdf = new jsPDF({
            orientation: 'landscape',
            unit: 'px',
            format: [2598, 1654]
        });



        pdf.advancedAPI(); // 切换到 "advanced" 模式

        // const year = `${new Date().getFullYear()}`;
        // const month = `${new Date().getMonth() + 1}`.padStart(2, '0');
        // const day = `${new Date().getDate()}`.padStart(2, '0');
        const { year, month, day } = futureDateDetails; // Destructure for ease of access
        const CarPlate = 'AC123';

        pdf.addImage('/car-plate.png', 'PNG', 0, 0, 2598, 1654);

        pdf.setFontSize(250);
        pdf.text(`${year}`, 400, 200, { align: 'center' });
        pdf.text(`${month}`, 1050, 200, { align: 'center' });
        pdf.text(`${day}`, 1550, 200, { align: 'center' });

        pdf.setFontSize(1300);
        const verticalStretchMatrix = new pdf.Matrix(0.5, 0, 0, 1, 0, 0); // 垂直方向拉伸2倍
        pdf.setCurrentTransformationMatrix(verticalStretchMatrix); // 拉伸
        pdf.text(`${CarPlate}`, 1900, 1270, ); // 生成车牌号码
        pdf.compatAPI(); // 切换回 "compat" 模式

        const pdfBlob = pdf.output('blob');
        const url = URL.createObjectURL(pdfBlob);
        setPdfUrl(url);
    };

    return (
        <div>
            <button onClick={generatePdf}>Generate PDF</button>
            {pdfUrl && <iframe src={pdfUrl} style={{ height: '1000px', width: '100%' }} />}
        </div>
    );
}

function FutureDateDetails() {
    const [futureDateDetails, setFutureDateDetails] = useState(() => {
        const today = new Date();
        const futureDate = new Date(today);
        futureDate.setDate(today.getDate() + 14);

        // Extract the year, month, and day
        const year = futureDate.getFullYear();
        const month = futureDate.getMonth() + 1; // adding 1 to normalize month to 1-based index
        const day = futureDate.getDate();

        return { year, month, day };
    });
}

export default PDFGenerator;
