import React, { useState } from 'react';
import { jsPDF } from 'jspdf';

function PDFGenerator() {
    const [pdfUrl, setPdfUrl] = useState('');

    const generatePdf = () => {
        const pdf = new jsPDF({
            orientation: 'landscape',
            unit: 'px',
            format: [2598, 1654]
        });

        const year = `${new Date().getFullYear()}`;
        const month = `${new Date().getMonth() + 1}`.padStart(2, '0');
        const day = `${new Date().getDate()}`.padStart(2, '0');
        const CarPlate = 'A1234';

        pdf.addImage('/carplate.png', 'PNG', 0, 0, 2598, 1654);



        pdf.setFontSize(250);
        pdf.text(`${year}`, 400, 200, { align: 'center' });
        pdf.text(`${month}`, 1050, 200, { align: 'center' });
        pdf.text(`${day}`, 1550, 200, { align: 'center' });

        // pdf.addFileToVFS("./Roboto-Regular.ttf", "fontData");
        // pdf.addFont('Roboto-Regular.ttf', 'custom', 'normal');
        // pdf.setFont("custom");
        pdf.setFontSize(1000);
        pdf.advancedAPI(); // 切换到 "advanced" 模式
        const verticalStretchMatrix = new pdf.Matrix(1, 0, 0, 0.5, 0, 0); // 垂直方向拉伸2倍
        pdf.setCurrentTransformationMatrix(verticalStretchMatrix);
        pdf.text(`${CarPlate}`, 500, 2000, );
        pdf.compatAPI(); // 切换回 "compat" 模式

        const pdfBlob = pdf.output('blob');
        const url = URL.createObjectURL(pdfBlob);
        setPdfUrl(url);
    };

    return (
        <div>
            <button onClick={generatePdf}>Generate PDF</button>
            {pdfUrl && <iframe src={pdfUrl} style={{ height: '500px', width: '100%' }} />}
        </div>
    );
}

export default PDFGenerator;
